package com.kita.first;

public class Mission6_7 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int star=5;
	
		 for(int i=1;i<=star;i++) {
			
			   
			 for(int j=star;j>0; j--) {
				 
				 if(i<j) {
					 System.out.print(" ");
				 }else {
						System.out.print("*");
				 }
				 
				
			
			
			 }
			
			 System.out.println();
		 }
		 
		 
		   
			
	}

}
